void gorilla42(u8* dst, u8* src) {
   u8 c,nc,m,h;
   i16 i,j,k;

   c = 0;
   for (i = 0xff; i >= 0; i--) {
      dst[i] = map[src[i] ^ ((src[i] >> 1) | c)];
      c = src[i] << 7;
   }
   for (i = 0; i < 8; i++) {
      for (j = 0; j < 0x100; j++)
         src[j] = 0;
      for (j = i*0x100; j < (i+1)*0x100; j++) {
         for (m = 1; m != 0; m <<= 1) {
            if (map[data[j]] & m)
               for (k = 0; k < 0x100; k++)
                  src[k] ^= dst[k];
            h = (~dst[0xff] & 0x80) >> 4;
            c = 0;
            for (k = 0; k < 0x100; k++) {
               nc = dst[k] >> 7;
               dst[k] = ((dst[k] << 1) | c) ^ (special[k] >> h);
               c = nc;
            }
         }
      }
      c = 0;
      for (j = 0xff; j >= 0; j--) {
         dst[j] = map[src[j] ^ ((src[j] >> 1) | c)];
         c = src[j] << 7;
      }
   }
}

