// replace *only* the initialization of this `input` array and *nothing else*,
// and have the output of this exact code have "Solved: Yes" at the bottom of
// its output. You are obviously free to write code to generate this input, but
// any potential solution should be tested in this file as a definitive check.

// Only modify this...
u8 input[0x100] = "This is a sample input. They are 256 element byte arrays. These bytes can be text or any kind of wild stuff. This sample input is only an example, and does not pertain to the solution mentioned in the expected output. To see a sample output, run this code.";

// ... so that it outputs this.
u8 expected_output[0x100] = "The goal of the gorilla42 puzzle is to modify the input array so that when it is passed into the gorilla42 function, it returns this very array of bytes (as text). There is a \"true solution\", but it does not matter how you find the right input. Good luck!!";

int main() {
   printf("Input:\n");
   for (i16 i = 0; i < 0x100; i++) {
      printf("%c", input[i]);
   }

   u8 output[0x100];
   gorilla42(output, input);

   printf("\n\nOutput:\n");
   u8 solved = 1;
   for (i16 i = 0; i < 0x100; i++) {
      printf("%c", output[i]);
      if (solved && (output[i] != expected_output[i])) {
         solved = 0;
      }
   }

   printf("\n\nExpected:\n");
   for (i16 i = 0; i < 0x100; i++) {
      printf("%c", expected_output[i]);
   }

   printf("\n\nSolved: %s\n", solved ? "Yes" : "No");
   return solved ? 0 : 1;
}

